SELECT distinct  ?person ?matter ?matterName (group_concat(distinct ?transactionTypeName; separator = '; ') as ?transactionTypes) ?totalHoursRecorded ?wIPNCRFees ?pcbBilled WHERE{ ?person a <http://data.linklaters.com/Person> . ?person <http://data.linklaters.com/isMatterManagerOfMatter> ?matter . 
?matter <http://data.linklaters.com/matterName> ?matterName .
OPTIONAL{?matter  <http://data.linklaters.com/isMatterOfTransactionType> ?transactionType . ?transactionType <http://data.linklaters.com/transactionTypeName>  ?transactionTypeName }
OPTIONAL{ ?matter <http://data.linklaters.com/totalHoursRecorded> ?totalHoursRecorded .}
OPTIONAL{ ?matter <http://data.linklaters.com/wIPNCRFees> ?wIPNCRFees .}
OPTIONAL{ ?matter <http://data.linklaters.com/pcbBilled> ?pcbBilled .}
VALUES(?person){(%entity%)} 
} 
group by ?person ?matter ?matterName ?totalHoursRecorded ?wIPNCRFees ?pcbBilled

