group by ?person ?matter ?matterName ?totalHoursRecorded ?wIPNCRFees ?pcbBilled

SELECT   ?person ?client ?clientName ?linklatersSector ?linklatersSectorName ?clientType  ?clientTypeName  
 WHERE{  
  ?person a <http://data.linklaters.com/Person> .  
  ?person <http://data.linklaters.com/isManagingRelationshipPartnerOfClient> ?client .  
  ?client <http://data.linklaters.com/clientName> ?clientName . 
  OPTIONAL {?client <http://data.linklaters.com/isClientOfLinklatersSector> ?linklatersSector . ?linklatersSector <http://data.linklaters.com/sectorName> ?linklatersSectorName . }  
  OPTIONAL {?client <http://data.linklaters.com/isClientOfClientType> ?clientType . ?clientType <http://data.linklaters.com/clientTypeName> ?clientTypeName . }  
 VALUES(?person){(%entity%)} 
}
